import { useRenderElement } from '../utils/useRenderElement.js';

/**
 * Returns an object with a `renderElement` function that renders a Base UI element.
 *
 * @public
 */
export function useRender(params) {
  const {
    render,
    props,
    state,
    refs
  } = params;
  const {
    ref: intrinsicRefProp,
    ...intrinsicProps
  } = props || {};
  const renderElement = useRenderElement(undefined, {
    render
  }, {
    props: intrinsicProps,
    state,
    ref: [intrinsicRefProp, ...(refs || [])].filter(x => x != null),
    styleHooks: false
  });
  return {
    renderElement
  };
}